SELECT MATRL_SEQ ,MATRL_CD ,MATRL_NM ,MATRL_CTGRY ,(SELECT BASE_NM FROM TB_SYSTEM_COMMON_CODE B WHERE B.BASE_GROUP_CD='005' AND B.BASE_CD = A.MATRL_CTGRY) MATRL_CTGRY_NM ,MATRL_GUBUN ,(SELECT BASE_NM FROM TB_SYSTEM_COMMON_CODE B WHERE B.BASE_GROUP_CD='008' AND B.BASE_CD = A.MATRL_GUBUN) MATRL_GUBUN_NM ,MATRL_MODEL ,MATRL_THICKNESS ,MATRL_WIDTH ,PRODUCT_UNIT ,(SELECT BASE_NM FROM TB_SYSTEM_COMMON_CODE B WHERE B.BASE_GROUP_CD='006' AND B.BASE_CD = A.PRODUCT_UNIT) PRODUCT_UNIT_NM ,TM_UNIT ,(SELECT BASE_NM FROM TB_SYSTEM_COMMON_CODE B WHERE B.BASE_GROUP_CD='007' AND B.BASE_CD = A.TM_UNIT) TM_UNIT_NM ,INWHS_CORP_CD ,(SELECT DEAL_CORP_NM FROM TB_DEAL_CORP_ADM B WHERE B.DEAL_CORP_CD = A.INWHS_CORP_CD) INWHS_CORP_NM ,MATRL_GRAVITY ,ETC1 ,ETC2 ,USE_YN ,(SELECT BASE_NM FROM TB_SYSTEM_COMMON_CODE B WHERE B.BASE_GROUP_CD='004' AND B.BASE_CD = A.USE_YN) USE_YN_NM ,MATRL_DESC ,REG_ID ,REG_DATE ,UPD_ID ,UPD_DATE FROM TB_MATRL_CODE_ADM A WHERE 1=1
AND MATRL_CD = #{matrlCd}
AND MATRL_NM LIKE '%' +#{matrlNm}+ '%'
AND MATRL_CTGRY = #{matrlCtgry}
